import { useState } from "react";

const messages = [
  "Learn React ⚛️",
  "Apply for jobs 💼",
  "Invest your new income 🤑",
];

export default function App() {
 // const [step, setStep] = useState(1);
 let step = 1;

  function movePrevious() { 
    // if(step > 1){
    //   setStep(step - 1);
    // }
    return step - 1;
  }

  function moveNext() {
    // if(step < 3){
    //   setStep(step + 1);
    // }
    return step + 1;
  }
  return <div className="steps">
    <div className="numbers">
      <div className={moveNext() >= 1 ? "active" : ''}>1</div>
      <div className={step >= 2 ? "active" : ''}>2</div>
      <div className={step >= 3 ? "active" : ''}>3</div>
    </div>

    <p className="message">Step {step}: {messages[step - 1]}</p>

    <div className="buttons">
      <button style={{backgroundColor: '#7950f2', color: '#fff'}} onClick={movePrevious}>Previous</button>
      <button style={{backgroundColor: '#7950f2', color: '#fff'}} onClick={moveNext}>Next</button>
    </div>
  </div>
}
