import { useState } from "react";


const messages = [
  "Learn React ‚öõÔ∏è",
  "Apply for jobs üíº",
  "Invest your new income ü§ë",
];

export default function App() {
  return <div>
    <Step />
  </div>
 }

 function Step() {
  const [step, setStep] = useState(1);
 const [isOpen, setIsOpen] = useState(true);

  function movePrevious() { 
    if(step > 1){
      setStep((s) => s-1);
    }
  }

  function moveNext() {
    if(step < 3){
      setStep((s) => s+1);
    }
  }
  return (
  <>
    <button className="close" onClick={()=>{setIsOpen((is)=> !isOpen)}}>&times;</button>
    {isOpen && (
    <div className="steps">
      <div className="numbers">
        <div className={step >= 1 ? "active" : ''}>1</div>
        <div className={step >= 2 ? "active" : ''}>2</div>
        <div className={step >= 3 ? "active" : ''}>3</div>
      </div>

      {/* <p className="message">
        <h3>Step {step} </h3>{messages[step - 1]}
      </p> */}
      <p className="message">
        <StepMessage step={step}>{messages[step - 1]}</StepMessage>
      </p>

      <div className="buttons">

      <Button textColor='#fff' bgColor='#7950f2' onClick={movePrevious}><span>üëà</span>Previous</Button>
      <Button textColor='#fff' bgColor='#7950f2' onClick={moveNext}>Next<span>üëâ</span></Button>
      </div>
    </div>
    )}
  </>
)}

function StepMessage({step, children}) {
  <p className="message">
  <h3>Step {step} </h3>
  {children}
</p>
}

function Button({textColor, bgColor, onClick, children}) {
  return <button style={{backgroundColor: bgColor, color: textColor}} 
  onClick={onClick}>
  {children}
  </button>
}
